import "./Form.css";
import { useNavigate } from "react-router-dom";
import React, { useState } from "react";
import VSpacer from "./VSpacer";
// import jsonData from './data.json';

function Form(props) {
  const navigate = useNavigate();
  const [userId, setUserId] = useState("");
  const [id, setId] = useState("");
  const [title, setTitle] = useState("");
  const [completed, setCompleted] = useState("");

  const changeUserId = (event) => {
    setUserId(event.target.value);
  };

  const changeId = (event) => {
    setId(event.target.value);
  };

  const changeTitle = (event) => {
    setTitle(event.target.value);
  };

  const changeCompleted = (event) => {
    setCompleted(event.target.value);
  };

  const transferValue = (event) => {
    event.preventDefault();
    const val = {
      userId,
      id,
      title,
      completed,
    };
    props.func(val);
    clearState();
  };

  const clearState = () => {
    setUserId("");
    setId("");
    setTitle("");
    setCompleted("");
  };

  return (
    <>
      <div>
        <label>userId</label>
        <input type="text" value={userId} onChange={changeUserId} />
        <label>id</label>
        <input type="text" value={id} onChange={changeId} />
        <label>City</label>
        <input type="text" value={title} onChange={changeTitle} />
        <label>City</label>
        <input type="text" value={completed} onChange={changeCompleted} />
        <button onClick={transferValue}> Click Me</button>
      </div>
      <VSpacer factor={4} />
      <table className="table">
        <thead>
          <tr>
            <th>userId</th>
            <th>id</th>
            <th>title</th>
            <th>completed</th>
          </tr>
        </thead>
        <tbody>{tableRows}</tbody>
        {/* <tbody>hi</tbody> */}
        <tbody>rows</tbody>
      </table>
    </>
  );
}

export default Form;
